import { Morph, Text } from "lively.morphic";
import { connect, disconnect } from "lively.bindings/index.js";
import { arr, date } from "lively.lang/index.js";
import { loadObjectFromPartsbinFolder } from "lively.morphic/partsbin.js";
import { pt, Color } from "lively.graphics/index.js";

export default class Comment extends Morph {

  static get properties() {
    return {
      replies: { defaultValue: [] },
      user: {
        get() { return this._user },
        set(u) { 
          this._user = u ;
          this.updateUserDisplay();
        }
      },
      haloShadow: {
        defaultValue: {
          blur: 6,
          color: Color.rgb(52,152,219),
          distance: 0,
          rotation: 45
        }
      },
      date: {
        after: ['submorphs'],
        get() { return this.get('date').textString },
        set(t) {
          this.get('date').textString = date.format(t, 'mmmm d yyyy');
        }
      }
    }
  }

  async renderComment({user, date, content, replies}) {
    this.setContent(content || new Text({textString: "Enter text...", padding: 5}));
    this.date = date || new Date();
    if (user) this.user = user;
    if (replies) {
      for (var r in replies) {
        this.registerReply(await (await loadObjectFromPartsbinFolder('Reply')).renderReply(r));
      }
    }
    return this;
  }

  updateUserDisplay() {

  }

  relayout() {
    const replies = this.get('reply container'),
          content = this.get('content');
    if (!content) return;
    this.height = content.textBounds().height + 100
    replies.position = this.innerBounds().bottomLeft()
    replies.width = this.width;
  }

  setContent(textMorph) {
    var content = this.get('content');
    if (content) {
      content.remove();
      disconnect(content, 'extent', this, 'relayout');
    }
    content = textMorph;
    content.name = 'content';
    this.addMorph(content);
    this.layout.row(1).col(0).group.morph = content;
    connect(content, 'extent', this, 'relayout');
  }

  async addReply() {
    var reply = await loadObjectFromPartsbinFolder('Reply');
    this.get('reply container').addMorph(reply);
    reply.setContent(new Text({textString: "Enter text...", padding: 5}));
    reply.date = new Date();
    this.showReplies();
    try {
      await reply.prepareToPost();
      this.registerReply(reply);
    } catch(err) {
      reply.remove();
      this.hideReplies();
    }
  }

  registerReply(reply) {
    this.replies.push(reply.post());
    if (reply.owner != this.get('reply container'))
      this.get('reply container').addMorph(reply);
    this.get('show replies').activeMode = 'active';
  }

  prepareToPost() {
    return new Promise((resolve, reject) => {
      this.hideButtons('edit button', 'show replies', 'reply button', 'date', 'cancel edit', 'save changes');
      this.showButtons('post comment', 'discard draft');
      this.get('post comment').action = resolve;
      this.get('discard draft').action = reject;
      this.get('controls').layout.apply();
      this.get('content').animate({dropShadow: this.haloShadow, readOnly: false, duration: 300});
    })
  }

  post() {
    this.get('post comment').action = null;
    this.get('discard draft').action = null;
    this.hideButtons('post comment', 'discard draft');
    this.showButtons('edit button', 'show replies', 'reply button', 'date');
    this.get('controls').layout.apply({duration: 300});
    this.get('content').animate({
      dropShadow: false, readOnly: true, duration: 300
    })
    return this;
  }

  toggleReplies() {
    this.repliesVisible ? this.hideReplies() : this.showReplies();
  }

  showReplies() {
    const replies = this.get('reply container'),
          toggler = this.get('show replies');
    if (!toggler.showReplies) toggler.toggle();
    this.repliesVisible = true;
    replies.layout.enable({duration: 300});
    replies.animate({
      opacity: 1, duration: 300
    });
  }

  hideReplies() {
    const toggler = this.get('show replies');
    this.repliesVisible = false;
    if (toggler.showReplies) toggler.toggle();
    this.get('reply container').layout.disable();
    this.get('reply container').animate({height: 0, opacity: 0, duration: 300});
  }

  enableEdit() {
    this.hideButtons('edit button');
    this.showButtons('cancel edit', 'save changes');
    this.get('controls').layout.apply({duration: 300});
    this.get('content').animate({dropShadow: this.haloShadow, readOnly: false, duration: 300});
  }

  cancelEdit() {
    this.disableEdit();
    // TODO: also discard changes
  }

  saveChanges() {
    this.disableEdit();
    // store change information?
  }

  disableEdit() {
    this.showButtons('edit button')
    this.hideButtons('cancel edit', 'save changes');
    this.get('controls').layout.apply({duration: 300});
    this.get('content').animate({dropShadow: false, readOnly: true, duration: 300});
  }

  showButtons(...toBeShown) {
    toBeShown.forEach(name => {
      this.get(name).animate({opacity: 1, visible: true, isLayoutable: true, duration: 300});
    });
  }

  hideButtons(...toBeHidden) {
    toBeHidden.forEach(name => {
      this.get(name).animate({opacity: 0, visible: false, isLayoutable: false, duration: 300});
    })
  }
}

