import { Morph, Text } from "lively.morphic";
import { connect, disconnect } from "lively.bindings/index.js";
import { arr, date } from "lively.lang/index.js";
import { loadObjectFromPartsbinFolder } from "lively.morphic/partsbin.js";
import { pt, Color } from "lively.graphics/index.js";
import { serialize, deserialize } from "lively.serializer2/index.js";
import { hashCode } from "lively.lang/string.js";

export default class Comment extends Morph {

  static get properties() {
    return {
      haloShadow: {
        defaultValue: {
          blur: 6,
          color: Color.rgb(52,152,219),
          distance: 0,
          rotation: 45
        }
      },
      replies: { defaultValue: [] },
      author: {
        get() { return this._author },
        set(a) { 
          this._author = a ;
          this.updateAuthorDisplay();
        }
      },
      user: {
        get() { return this._user },
        set(u) {
          this._user = u;
          this.replies.forEach(r => r.user = u);
          this.ensureEditPrivileges();
        }
      },
      parent: {
         /* the hash to the comment or reply before */
        get() { return this._parent },
        set(p) { 
          if (p.hash) {
            this._parent = p.hash;
          } else {
            this._parent = p;
          }
        }
      },
      date: {
        after: ['submorphs'],
        get() { return this.get('date').textString },
        set(t) {
          this.get('date').textString = date.format(t, 'mmmm d yyyy');
        }
      }
    }
  }

  ensureEditPrivileges() {
    this.user.name == this.author.name ? this.showButtons('edit button') : this.hideButtons('edit button');
    this.get('controls').layout.apply();
  }

  async renderComment(user, {author, date, hash, content, replies, parent}) {
    this.setContent(deserialize(content));
    this.author = author;
    this.user = user;
    this.date = date; this.hash = hash; this.parent = parent;
    if (replies) {
      for (var r of replies) {
        this.registerReply(await (await loadObjectFromPartsbinFolder('Reply')).renderReply(user, r));
      }
    }
    return this;
  }

  getData() {
    return {
      author: this.author,
      content: serialize(this.getContent()),
      date: this.date,
      hash: this.hash,
      parent: this.parent,
      replies: this.replies.map(r => r.getData())
    }
  }

  updateAuthorDisplay() {
    this.get('author avatar').submorphs = [this.author.avatar.copy()];
    this.get('author name').textString = this.author.name;
    this.get('edit button').activeMode = this.user == this.author ? 'active' : 'inactive';
    this.get('title bar').layout.col(1).width = this.get('author info').bounds().width;
    this.relayout();
  }

  relayout() {
    const replies = this.get('reply container'),
          content = this.get('content');
    if (!content) return;
    this.height = content.textBounds().height + 100
    replies.position = this.innerBounds().bottomLeft()
    replies.width = this.width;
    this.replies.forEach(r => r.width = this.width - 10);
  }

  setContent(textMorph) {
    var content = this.get('content');
    if (content) {
      content.remove();
      disconnect(content, 'extent', this, 'relayout');
    }
    content = textMorph;
    content.name = 'content';
    this.addMorph(content);
    this.layout.row(1).col(0).group.morph = content;
    connect(content, 'extent', this, 'relayout');
  }

  getContent() {
    disconnect(this.get('content'), 'extent', this, 'relayout');
    const content = this.get('content').copy()
    connect(this.get('content'), 'extent', this, 'relayout');
    return content;
  }

  async addReply() {
    var reply = await loadObjectFromPartsbinFolder('Reply');
    this.get('reply container').addMorph(reply);
    reply.setContent(new Text({textString: "Enter text...", padding: 5}));
    reply.date = new Date();
    reply.user = reply.author = this.user;
    reply.width = this.width - 10;
    this.showReplies();
    try {
      await reply.prepareToPost(this);
      this.registerReply(reply);
    } catch(err) {
      reply.remove();
      this.hideReplies();
    }
  }

  registerReply(reply) {
    // ensure that we have the most recent version of the
    // topic conversation, by triggering an update beforehand and then
    // inserting the new comment.
    // conflicts may still arise and need to be resolved by the server 
    this.replies.push(reply.post(arr.last(this.replies) || this));
    if (reply.owner != this.get('reply container'))
      this.get('reply container').addMorph(reply);
    this.get('show replies').activeMode = 'active';
  }

  prepareToPost() {
    return new Promise((resolve, reject) => {
      this.hideButtons('edit button', 'show replies', 'reply button', 'date', 'cancel edit', 'save changes');
      this.showButtons('post comment', 'discard draft');
      this.get('post comment').action = resolve;
      this.get('discard draft').action = reject;
      this.get('controls').layout.apply();
      this.get('content').animate({dropShadow: this.haloShadow, readOnly: false, duration: 300});
    })
  }

  post(parent) {
    // compute a hash consisting out of the preceding replies / comments hash
    // and the users name hash(prev.hash + author.name)
    this.parent = parent.hash;
    this.hash = hashCode(this.parent.toString() + this.date.toString() + this.author.name);
    this.get('post comment').action = null;
    this.get('discard draft').action = null;
    this.hideButtons('post comment', 'discard draft');
    this.showButtons('edit button', 'show replies', 'reply button', 'date');
    this.get('controls').layout.apply({duration: 300});
    this.get('content').animate({
      dropShadow: false, readOnly: true, duration: 300
    })
    return this;
  }

  toggleReplies() {
    this.repliesVisible ? this.hideReplies() : this.showReplies();
  }

  showReplies() {
    const replies = this.get('reply container'),
          toggler = this.get('show replies');
    if (!toggler.showReplies) toggler.toggle();
    this.repliesVisible = true;
    replies.layout.enable({duration: 300});
    replies.animate({
      opacity: 1, duration: 300
    });
  }

  hideReplies() {
    const toggler = this.get('show replies');
    this.repliesVisible = false;
    if (toggler.showReplies) toggler.toggle();
    this.get('reply container').layout.disable();
    this.get('reply container').animate({height: 0, opacity: 0, duration: 300});
  }

  enableEdit() {
    this.tmpContent = this.getContent();
    this.hideButtons('edit button');
    this.showButtons('cancel edit', 'save changes');
    this.get('controls').layout.apply({duration: 300});
    this.get('content').animate({dropShadow: this.haloShadow, readOnly: false, duration: 300});
  }

  cancelEdit() {
    this.disableEdit();
    this.setContent(this.tmpContent);
    // TODO: also discard changes
  }

  saveChanges() {
    this.disableEdit();
    // store change information?
  }

  disableEdit() {
    this.showButtons('edit button')
    this.hideButtons('cancel edit', 'save changes');
    this.get('controls').layout.apply({duration: 300});
    this.get('content').animate({dropShadow: false, readOnly: true, duration: 300});
  }

  showButtons(...toBeShown) {
    toBeShown.forEach(name => {
      this.getSubmorphNamed(name).animate({opacity: 1, visible: true, isLayoutable: true, duration: 300});
    });
  }

  hideButtons(...toBeHidden) {
    toBeHidden.forEach(name => {
      this.getSubmorphNamed(name).animate({opacity: 0, visible: false, isLayoutable: false, duration: 300});
    })
  }
}

