import { Button } from "lively.morphic/components/buttons.js";
import { num } from "lively.lang/index.js";
import { pt, Color } from "lively.graphics/index.js";
import { connect, disconnect } from "lively.bindings/index.js";

export default class Show_replies extends Button {

  static get properties() {
     return {
        activeColor: {defaultValue: Color.rgb(68,138,255)}
     }
  }

  toggle() {    
    this.showReplies = !this.showReplies;
    this.get('toggle indicator').animate({
       ...this.showReplies ? {
          rotation: num.toRadians(90),
       } : {
          rotation: 0
       },
       duration: 300
    })
    this.get('active').animate({
       opacity: this.showReplies ? 1 : 0,
       fontColor: this.activeColor, 
       duration: 300
    });

    this.get('label text').fontColor = this.showReplies ? 
         this.activeColor : Color.black
  }

  trigger() {
     this.toggle();
     super.trigger();
  }

}
