import { Morph } from "lively.morphic/morph.js";
import { connect, disconnect } from "lively.bindings/index.js";
import { pt, rect, Color } from "lively.graphics/index.js";
import { loadObjectFromPartsbinFolder } from "lively.morphic/partsbin.js";
import { deserialize } from "lively.serializer2/index.js";

export default class Discussion extends Morph {

  static get properties() {
    return {
      topics: {defaultValue: []},
      user : {
        after: ['submorphs'],
        set(u) {
           this._user = u;
           const userName = this.get('search bar').get('user name');
           userName.textString = u.name;
           let avatar = u.avatar.copy();
           avatar.center = this.get('logged in user avatar').innerBounds().center();
           this.get('logged in user avatar').submorphs = [avatar];
           this.get('search bar content').layout.col(2).width = userName.textBounds().width + 15;
           this.relayout();
        },
        get() {
         return this._user;
        }
      }
    }
  }

  onWindowClose() {
    this.get('globe').stopAnimation();
  }

  beforePublish() {
    this.showWelcomeScreen();
    this.get('User Widget').clearLogin();
    this.get('cover').opacity = 1;
    this.get('cover').visible = true;
    this.topics.forEach(t => t.remove());
    this.topics = [];
  }

  onLoad() {
    const font = document.createElement('link');
    font.setAttribute('href', 'https://fonts.googleapis.com/css?family=Roboto');
    font.setAttribute('rel', 'stylesheet');
    font.setAttribute('type', 'text/css');
    document.head.append(font);
    this.showWelcomeScreen();
    this.get('cover').animate({opacity: 0, visible: false});
  }

  reset() {
    connect(this.get("comment-text"), "selectionChange", this.get("comment-text"), "openRichTextControl");
  }

  get __only_serialize__() {
    return [...super.__only_serialize__, 'topicShadow', 'activeWindowFill']
  }

  filterTopics() {
    let ts = this.get('TopicSearch'),
        tc = this.get('topics container'),
        searchInput = ts && ts.input.toLowerCase();
    if (!searchInput) return;
       return tc.submorphs = this.topics;
    tc.submorphs = this.topics.filter(t => 
       t.title.toLowerCase().includes(searchInput));
  }

  relayout(duration) {
    // fixme: this is a good example of where an animation context would be very useful
    this.get('connect icon').bottomRight = this.innerBounds().bottomRight()
    this.get('create topic').bottomLeft = this.innerBounds().insetBy(10).bottomLeft();
    if (duration) {
       this.get('User Widget').animate({center: this.extent.scaleBy(.5).roundTo(1), duration});
    } else {
       this.get('User Widget').center = this.extent.scaleBy(.5).roundTo(1);
    }
    this.get('topics container').submorphs.forEach(t => t.width = this.width - 10);
    this.get('search bar').width = this.width;
    this.get('close login button').topRight = this.innerBounds().insetBy(10).topRight();
    this.get('flap shadow').width = this.width - 20;
    this.get('flap shadow').bottomCenter = this.get('search bar content').bottomCenter.addXY(0,-5);
    if (this.draftedTopic) 
      this.draftedTopic.center = this.innerBounds().center();
  }

  async createTopic() {
    const topic = await loadObjectFromPartsbinFolder("Topic"),
          container = this.get('topics container'),
          self = this;
    this.draftedTopic = topic;
    topic.isLayoutable = false;
    topic.dropShadow = true;
    this.addMorph(topic);
    topic.width = this.width - 50;
    topic.author = this.user;
    topic.date = new Date();
    topic.center = this.innerBounds().center();
    topic.dropShadow = this.topicShadow;
    connect(topic, 'expanding', this, 'expandTopic');
    connect(topic, 'collapsing', this, 'collapseTopic');
    connect(topic, 'extent', this, 'relayout')
    await this.get('shadow overlay').animate({opacity: 1, visible: true, duration: 300});
    try {
      await topic.prepareForCreation();
      // topic.isLayoutable = true; 
          topic.user = this.user;
      topic.dropShadow = false;
      container.layout.disable();
      container.addMorph(topic);
      this.topics.push(topic.create());
      container.layout.enable({duration: 300});
    } catch (error) {
      topic.remove();
    }
    this.draftedTopic = null;
    disconnect(topic, 'extent', this, 'relayout');
    this.get('shadow overlay').animate({opacity: 0, visible: false, duration: 300});
  }

  async showLogin() {
    const userWidget = this.get('User Widget');
    if (this.expandedTopic) {
       this.expandedTopic.collapse();
    }
    userWidget.clearLogin();
    this.get('shadow overlay').animate({opacity: 1, visible: true, duration: 300});
    userWidget.adjustOrigin(userWidget.extent.scaleBy(.5));
    userWidget.scale = 1.2;
    await userWidget.animate({opacity: 1, visible: true, scale: 1, duration: 300});
    userWidget.adjustOrigin(pt(0,0));
  }

  hideLogin() {
    this.get('User Widget').animate({opacity: 0, visible: false, duration: 300});
    this.get('shadow overlay').animate({opacity: 0, visible: false, duration: 300});
  }

  expandTopic(topic) {
    this.expandedTopic = topic;
    topic.user = this.user;
    topic.morphIndex = this.get('topics container').submorphs.indexOf(topic)
    this.addMorph(topic);
    connect(this, 'extent', topic, 'extent', {converter: (ext) => ext.addXY(15, -25)});
    topic.animate({position: pt(0,40), width: this.width - 15, height: this.height - 25, duration: 300});
  }

  collapseTopic(topic) {
    disconnect(this, 'extent', topic, 'extent');
    this.get('topics container').addMorphAt(topic, topic.morphIndex);
    this.expandedTopic = null;
  }

  showWelcomeScreen() {
    const globe = this.get('globe');
    if (!globe.renderLoop) globe.renderGlobe();
    globe.animate({visible: true, opacity: 1});
    this.owner && this.owner.animate({fill: Color.black, borderColor: Color.gray.darker()});
  }

  hideWelcomeScreen() {
    this.get('globe').animate({visible: false, opacity: 0});
    this.owner.animate({fill: this.activeWindowFill, borderColor: Color.gray});
  }

}

