import { Canvas } from "lively.morphic/components/canvas.js";
import { Scene, SpotLight, MeshBasicMaterial, BackSide, ImageUtils, Color, MeshPhongMaterial, SphereGeometry, Mesh, DirectionalLight, AmbientLight, WebGLRenderer, PerspectiveCamera } from "lively.morphic/node_modules/three/build/three.js";

export default class Globe extends Canvas {

  relayout() {
    this.get('welcome title').topCenter = this.innerBounds().insetBy(30).topCenter();
    this.renderer.setSize(this.width, this.height)
    this.get('connect button').center = this.innerBounds().center();
    this.camera.aspect = this.width / this.height;
    this.camera.updateProjectionMatrix()
  }

  stopAnimation() {
    if (this.renderLoop) cancelAnimationFrame(this.renderLoop);
  }

  renderGlobe() {
    this.stopAnimation();
    this.withCanvasDo(webglEl => {
    	var width  = this.width,
    		   height = this.height,
         textureDir = 'lively.morphic/assets/globe-textures/',
         self = this;

    	// Earth params
    	var radius   = 0.5,
    		   segments = 32,
    		   rotation = 5.2;  

    	var scene = new Scene();

    	var camera = new PerspectiveCamera(45, width / height, 0.01, 1000);
    	camera.position.z = 1;
     camera.position.x = .4;
     camera.position.y = .25;
     this.camera = camera;

    	this.renderer = new WebGLRenderer({canvas: webglEl});
    	this.renderer.setSize(width, height);

    	scene.add(new AmbientLight(0x333333));

    	var light = new SpotLight(0xffffff, 1, 200, Math.PI/2, 1);
    	light.position.set(1,1,0);
     light.castShadow = true;
     light.shadow.mapSize.width = 1024;  // default 512
     light.shadow.mapSize.height = 1024; // default 512
     light.shadow.camera.near = 10;       // default 0.5
     light.shadow.camera.far = 100;
     light.shadow.bias = 0.01
    	scene.add(light);

        var sphere = createSphere(radius, segments);
    	sphere.rotation.y = rotation; 
    	scene.add(sphere)

        var clouds = createClouds(radius, segments);
    	clouds.rotation.y = rotation;
    	scene.add(clouds)

    	var stars = createStars(90, 64);
    	scene.add(stars);

     this.limitLoop(render, 15);

    	function render() {
    		sphere.rotation.y += 0.0005;
    		clouds.rotation.y += 0.0005;		
    		self.renderer.render(scene, camera);
    	};

    	function createSphere(radius, segments) {
    		return new Mesh(
    			new SphereGeometry(radius, segments, segments),
    			new MeshPhongMaterial({
    				map:         ImageUtils.loadTexture(textureDir + '2_no_clouds_4k.jpg'),
    				bumpMap:     ImageUtils.loadTexture(textureDir + 'elev_bump_4k.jpg'),
    				bumpScale:   0.005,
        shininess: 5,
    				specularMap: ImageUtils.loadTexture(textureDir + 'water_4k.png'),
    				specular:    new Color('grey')								
    			})
    		);
    	}

    	function createClouds(radius, segments) {
    		return new Mesh(
    			new SphereGeometry(radius + 0.003, segments, segments),			
    			new MeshPhongMaterial({
    				map:         ImageUtils.loadTexture(textureDir + 'fair_clouds_4k.png'),
    				transparent: true
    			})
    		);		
    	}

    	function createStars(radius, segments) {
    		return new Mesh(
    			new SphereGeometry(radius, segments, segments), 
    			new MeshBasicMaterial({
    				map:  ImageUtils.loadTexture(textureDir + '/galaxy_starfield.png'), 
    				side: BackSide
    			})
    		);
    	}
    });
  }

  limitLoop(fn, fps) {

    // Use var then = Date.now(); if you
    // don't care about targetting < IE9
    var then = new Date().getTime(),
        self = this;

    // custom fps, otherwise fallback to 60
    fps = fps || 60;
    var interval = 1000 / fps;

    return (function loop(time){
        self.renderLoop = requestAnimationFrame(loop);

        // again, Date.now() if it's available
        var now = new Date().getTime();
        var delta = now - then;

        if (delta > interval) {
            // Update time
            // now - (delta % interval) is an improvement over just 
            // using then = now, which can end up lowering overall fps
            then = now - (delta % interval);

            // call the fn
            fn();
        }
    }(0));
};
}

