    this.widgetMode = 'register';
    this.showMorphs('back button', 'create user title');
    this.get('create user title').topCenter = this.innerBounds().insetBy(20).topCenter();
    this.hideMorphs('login title', 'new user button');
    this.get('Create User Controls').animate({
