       duration: 300
    })
    this.get('Login User Controls').animate({
       topRight: this.innerBounds().topLeft().addXY(0,50),
       opacity: 0,
       duration: 300
    })
    this.animate({
       height: 470,
       duration: 300
    });
    this.get('Discussion').relayout(300);
  }

  async createUser() {
    const userNameField = this.get('user name input 2'),
          emailField = this.get('user email'),
          pwField = this.get('create password input'),
          email = emailField.textString,
          userName = userNameField.textString,
          pw1 = pwField.input,
          avatar = this.get('AvatarEditor').getAvatarInfo();
    let user = await createUser({
        name: userName,
        email,
        password: pw1,
        avatar
      });
    emailField.clear();
    userNameField.clear();
    pwField.clear();
    this.get('retype password input').clear();
    this.get('AvatarEditor').cleanup();
    this.enterLogin();
  }

  async loginUser() {
    var user;
    if (user = await this.authenticateUser(this.get('user name').textString)) {
      this.get('Discussion').hideLogin();
      this.get('Discussion').hideWelcomeScreen();
      this.get('Discussion').user = user;
    } else {
      this.get('login password input').toggleWarning(true);
    }
  }

  async authenticateUser(userName) {
    var authUser = await getUser({email: this.get('user name').textString}),
        user = {
         ...authUser,
         username: 'Robin Schreiber',
         password: this.get('login password input').input};
    user.token = (await authenticateUser(user)).token;
    user.avatar = deserialize(JSON.parse(user.avatar));
    if (!user.token.error) {
      return user
    } else {
      return false;
    }
  }

  showMorphs(...morphs) {
    for (var b of morphs) {
      this.get(b).animate({opacity: 1, visible: true, duration: 300});
    }
  }

  hideMorphs(...morphs) {
    for (var b of morphs) {
      this.get(b).animate({opacity: 0, visible: false, duration: 300});
    }
  }
}

