import { Ellipse, morph } from "lively.morphic";
import { Color, pt } from "lively.graphics/index.js";
import { disconnectAll, connect } from "lively.bindings/index.js";
import { System as defaultSystem } from "lively.modules/index.js";
import { serialize } from "lively.serializer2/index.js";

export default class AvatarEditor extends Ellipse {

  moveImage(delta) {
    this.get('hidden part').moveBy(delta);
    this.get('avatar').moveBy(delta);
  }

  getAvatarInfo() {
    const {extent, imageUrl, position, origin} = this.get('avatar') || morph({
      type: 'image', extent: this.extent, 
    });
    return serialize(
      morph({
        type: 'ellipse',
        extent: this.extent,
        clipMode: 'hidden',
        fill: Color.transparent,
        submorphs: [{
          type: "image", imageUrl: imageUrl.replace(defaultSystem.baseURL, ''), 
          extent, position, origin
        }]
      }))
  }

  cleanup() {
    this.get('hidden part') && this.get('hidden part').remove();
    this.get('wrapper') && this.get('wrapper').remove();
    this.relayout();
  }

  async relayout() {
    if (this.get('wrapper')) {
    this.get('instructions').visible = false;
    } else {
    this.get('instructions').visible = true;
    }
  }

  async onMouseDown(evt) {
    if (evt.state.clickCount > 1) {
      this.transformMode = !this.transformMode;
    }
    if (this.transformMode) {
       if (evt.state.clickCount > 1) this.get('resize info').animate({opacity: 1});
       this.get('hidden part').visible = true;
       this.get('wrapper').borderStyle = 'dashed';
       this.get('wrapper').borderColor = Color.white;
    } else {
       this.get('resize info').opacity = 0;
       this.get('hidden part').visible = false;
       this.get('wrapper').borderStyle = 'solid';
       this.get('wrapper').borderColor = Color.gray;
    }
    if (this.transformMode && 
        evt.rightMouseButtonPressed() && 
        evt.state.clickCount < 2) {
      var {default: Halo} = await defaultSystem.import("lively.morphic/halo/morph.js");
      if (!this.get('resize handles')) {
        this.addMorph(new Halo({
           resizeOnly: true, name: 'resize handles', onDrag: (evt) => this.get('wrapper').onDrag(evt), 
           target: this.get('avatar')}));
        this.get('resize info').animate({opacity: 0});
      }
    }
    this.relayout();
  }

  onDrop(evt) {
    var wrapper = this.get('wrapper'),
        hiddenPart = this.get('hidden part');
    wrapper && wrapper.remove();
    hiddenPart && hiddenPart.remove();
    wrapper = this.addMorph({
      name: 'wrapper', tooltip: 'Double click to transform',
      type: 'ellipse', extent: this.extent, clipMode: 'hidden',
      fill: Color.transparent, 
      onDrag: (evt) => {
        if (this.transformMode) {
           this.moveImage(evt.state.dragDelta);
        } else if (evt.hand) {
          const target = this.get('avatar');
          target.reactsToPointer = true;
          evt.stop();
          evt.hand.grab(target)
          target.globalPosition = this.get('hidden part').globalPosition;
          this.transformMode = false;
          this.cleanup();
        }
      },
      borderWidth: 5, borderColor: Color.gray, submorphs: []
    }, this.get('resize info'));
    evt.hand.dropMorphsOn(wrapper);
    wrapper.submorphs[0].name = "avatar";
    var target = this.get('avatar');
    target.center = this.innerBounds().center();
