import { getUser } from "lively.user/index.js";
import { deserialize } from "lively.serializer2/index.js";

export default class AvatarView extends Ellipse {
  async showUser(userEmail) {
    var user = await getUser({email: userEmail}),
        avatar;
    if (user.name != 'anonymous') {
      avatar = this.addMorph(deserialize(JSON.parse(user.avatar)));
      avatar.center = this.innerBounds().center();
      avatar.opacity = 0;
      await avatar.animate({opacity: 1, duration: 300});
    }
    this.get('avatar') && this.get('avatar').remove();
    if (avatar) avatar.name = 'avatar';
  }
}

