import { Text } from "lively.morphic";
import { Color, pt, rect } from "lively.graphics/index.js";
import { getUser } from "lively.user/index.js";


export default class UserName extends Text {

  get __only_serialize__() {
    return [...super.__only_serialize__, 'warningColor', 'haloShadow']
  }

  toggleWarning(active) {
    if (active) {
      this.get('warning').visible = true;
      this.borderWidth = 3;
      this.padding = rect(6,6,0,0)
      this.borderColor = this.warningColor;
    } else {
      this.get('warning').visible = false;
      this.borderWidth = 1;
      this.padding = rect(8,8,0,0)
      this.borderColor = Color.gray;
    }
  }

  clear() {
    this.toggleWarning(false);
    this.textString = '';
    this.get('user name backdrop').visible = true;
  }

  async isValid() {
    return (await getUser({email: this.textString})).email == this.textString;
  }

  onKeyUp(evt) {
    super.onKeyUp(evt);
    this.get('user name backdrop').visible = this.textString == '';
  }

  onFocus(evt) {
    super.onFocus(evt);
    this.toggleWarning(false);
    this.animate({dropShadow: this.haloShadow, duration: 300});
  }

  async onBlur(evt) {
    super.onBlur(evt);
    this.scroll = pt(0,0);
    this.toggleWarning(this.textString && !await this.isValid());
    this.get('user avatar').showUser(this.textString);
    this.animate({dropShadow: false, duration: 300});
  }

}
