import { Morph, Canvas } from "lively.morphic";
import { pt, Color } from "lively.graphics/index.js";
import { connect } from "lively.bindings/index.js";
import { promise } from "lively.lang/index.js";
import { loadObjectFromPartsbinFolder } from "lively.morphic/partsbin.js";
import ObjectPackage from "lively.classes/object-classes.js";



export default class Palette extends Morph {
  static get properties() {
    return {
      uncollapsedExtent: {defaultValue: null},
    }
  }

  beforePublish() {
    this.hideSketch();
    this.uncollapse();
    let w = this.widthMorphs();
    let c = this.colorMorphs();
    this.selectColorMorph(c[1]);
    this.selectWidthMorph(w[0]);
    w[0].submorphs[0].fill = c[1].fill;
    w[1].submorphs[0].fill = c[0].fill;
    w[2].submorphs[0].fill = c[2].fill;
    w[3].submorphs[0].fill = c[4].fill;
  }

  onLoad(snapshot, rev) {
    const sketchPkg = ObjectPackage.packageStore["Sketch"];
    if (sketchPkg) {
      this.sketchClass = sketchPkg.objectClass;  
    } else {
      loadObjectFromPartsbinFolder("Sketch").then(
        (sketch) => this.sketchClass = sketch.constructor);
    }
  }

  get isSketchingPalette() { return true; }

  /***************************************************************/

  reset() {
    const widths = [2, 8, 16, 32];
    const colors = [
      Color.rgb(212,69,3),     // red
      Color.rgb(0,0,0),        // black
      Color.rgb(236,168,31),   // yellow
      Color.rgb(177,173,162),  // grey
      Color.rgb(79,124,129),   // cyan
      Color.rgb(246,246,246),  // white
      Color.rgb(145,161,112),  // green
      Color.rgb(171,127,88),   // brown
      Color.rgb(183,143,170),  // purple
    ];
    this.removeAllMorphs();
    this.buildButtons();
    this.buildWidths(widths);
    this.buildColors(colors);
    this.beforePublish();
  }

  /***************************************************************/
  /* Drawing Sketch
  /***************************************************************/

  hideSketch() {
    if (this.sketch) {
      this.sketch.remove();
      this.sketch = null;
    }
  }

  showSketch() {
    this.hideSketch();
    this.bringToFront();
    this.sketch = new this.sketchClass({
      extent: this.world().extent,
      fill: Color.white.withA(0.8),
      onDragStart: (evt) => this.sketch_onDragStart(evt),
      onDrag: (evt) => this.sketch_onDrag(evt),
      onDragEnd: (evt) => this.sketch_onDragEnd(evt),
    });
    this.world().addMorph(this.sketch, this);
  }

  sketch_onDragStart(evt) {
    if (evt.leftMouseButtonPressed()) {
      const {x, y} = this.sketch.localize(evt.position);
      this.sketch.strokes.push({
        color: this.penColor,
        width: this.penWidth,
        vertices: [x, y],
      });
    }
  }

  sketch_onDrag(evt) {
    if (evt.leftMouseButtonPressed()) {
      let from = this.sketch.localize(evt.state.lastDragPosition),
          to = this.sketch.localize(evt.position);
      this.drawLine(
        from,
        to,
        this.penColor,
        this.penWidth * evt.pressure * 2);
      let {strokes} = this.sketch,
          {vertices} = strokes[strokes.length - 1],
          {x, y} = to;
      vertices.push(x, y);
    }
  }

  sketch_onDragEnd(evt) {
    if (evt.leftMouseButtonPressed()) {
      this.sketch.redraw();
    }
  }

  drawLine(from, to, color, width) {
    let c = this.sketch.context;
    c.save();
    c.globalCompositeOperation = "darken";
    c.strokeStyle = color;
    c.beginPath();
    c.lineWidth = width;
    c.lineJoin = "round";
    c.lineCap = "round";
    c.moveTo(from.x, from.y);
    c.lineTo(to.x, to.y);
    c.stroke();
    c.restore();
  }

  get penColor() {
     return this.selectedWidthMorph().submorphs[0].fill;
  }

  set penColor(color) {
     this.selectedWidthMorph().submorphs[0].fill = color;
  }

  get penWidth() {
     return this.selectedWidthMorph().submorphs[0].width;
  }

  createSketch() {
    let {sketch} = this;
    sketch.trimNonTransparent();
    Object.assign(sketch, {
      name: 'Sketch',
      fill: null,
      scaleToBounds: true,
    });
    delete sketch.onDragStart;
    delete sketch.onDrag;
    this.sketch = null;
  }

  /***************************************************************/
  /* Buttons
  /***************************************************************/

  buildButtons() {
    this.addMorph({
      type: "button",
      name: 'newButton',
      position: pt(15, 24),
      extent: pt(40, 40),
      label: 'New',
    });
    this.addMorph({
      type: "button",
      name: 'clearButton',
      position: pt(95, 24),
      extent: pt(40, 40),
      label: 'Clear',
    });
    this.addMorph({
      type: "button",
      name: 'cancelButton',
      position: pt(95, 74),
      extent: pt(40, 40),
      label: 'Cancel',
    });
    this.addMorph({
      type: "button",
      name: 'okButton',
      position: pt(15, 74),
      extent: pt(50, 40),
      label: 'OK',
    });
    connect(this.newButton,    "fire", this, "doNew");
    connect(this.clearButton,  "fire", this, "doClear");
    connect(this.okButton,     "fire", this, "doOk");
    connect(this.cancelButton, "fire", this, "doCancel");
  }

  get newButton()    { return this.getSubmorphNamed('newButton')}
  get clearButton()  { return this.getSubmorphNamed('clearButton')}
  get okButton()     { return this.getSubmorphNamed('okButton')}
  get cancelButton() { return this.getSubmorphNamed('cancelButton')}

  doNew() {
    if (this.sketch) return;
    this.showSketch();
  }

  doClear() {
    if (!this.sketch) return;
    this.sketch.strokes = [];
    this.sketch.redraw();
  }

  doOk() {
    if (!this.sketch) return;
    this.createSketch();
    this.collapse();
  }

  doCancel() {
    this.hideSketch();
    this.collapse();
  }

  edit(sketch) {
    this.showSketch();
    let {position: {x, y}, context: {canvas}} = sketch;
    this.sketch.strokes = sketch.strokes.slice();
    this.sketch.moveStrokes(sketch.topLeft);
    //this.sketch.withContextDo((c) => c.drawImage(canvas, x, y));    
  }

  /***************************************************************/
  /* Color Palette 
  /***************************************************************/

  buildColors(colors) {
    colors.forEach((color, i) =>
      this.addMorph({
        type: "ellipse",
        name: `color${i}`,
        position: pt(400 + i * 30, 74 - (i&1) * 60),
        extent: pt(50, 50),
        fill: color,
        draggable: false,
        dropShadow: {inset: true, blur: 2},
        borderColor: Color.white,
      }));
    this.colorMorphs().forEach(
      (m) => connect(m, "onMouseDown", this, "selectColorMorph",
         {converter: '() => source'}));
  }

  selectedColorMorph() {
    return this.colorMorphs().find((m) => m.borderWidth > 0);
  }

  selectColorMorph(cMorph) {
    this.colorMorphs().forEach((m) => m.borderWidth = (m === cMorph) ? 4 : 0);
    this.penColor = this.selectedColorMorph().fill;
  }

  selectColor(color) {
    this.colorMorphs().forEach((m) => m.borderWidth = color.equals(m.fill) ? 4 : 0);
  }

  colorMorphs() {
    return this.submorphs.filter(({name}) => /^color[0-9]+$/.test(name));
  }

  /***************************************************************/
  /* Width Palette
  /***************************************************************/

  buildWidths(widths) {
    var x = 190;
    widths.forEach((width, i) => {
      this.addMorph({
        name: `width${i}`,
        position: pt(x, 22),
        extent: pt(width + 32, 100),
        borderRadiusLeft: [0].includes(i) ? 10 : 0,
        borderRadiusRight: [3].includes(i) ? 10 : 0,
        borderRadiusBottom: [0,3].includes(i) ? 10 : 0,
        borderRadiusTop: [0,3].includes(i) ? 10 : 0,
        dropShadow: {inset: true},
        draggable: false,
        submorphs: [
          {
              extent: pt(width, 100-16),
              borderRadius: width,
              position: pt(16, 8),
              fill: Color.black,
              dropShadow: true,
              draggable: false,
          }
        ] 
      });
      x += width + 32;
    });
    this.widthMorphs().forEach(
      (m) => connect(m, "onMouseDown", this, "selectWidthMorph",
         {converter: '() => source'}));    
  }

  selectedWidthMorph() {
    return this.widthMorphs().find((m) => m.fill.r === Color.white.r);
  }

  selectWidthMorph(wMorph) {
    this.widthMorphs().forEach((m) =>
      m.fill = (m === wMorph) ? Color.white : Color.darkGray);
    this.selectColor(this.penColor);
  }

  widthMorphs() {
    return this.submorphs.filter(({name}) => /^width[0-9]+$/.test(name));
  }

  /***************************************************************/
  /* Collapsing
  /***************************************************************/

  get isCollapsed() { return !!this.uncollapsedExtent; }

  collapse() {
    if (this.isCollapsed) return;
    this.uncollapsedExtent = this.extent;
    this.extent = pt(40, 40);
    for (let m of this.submorphs) m.visible = false;
  }

  uncollapse() {
    if (!this.isCollapsed) return;
    this.extent = this.uncollapsedExtent;
    this.uncollapsedExtent = null;
    for (let m of this.submorphs) m.visible = true;
  }

  onMouseDown(evt) {
    if (this.isCollapsed)
      this.uncollapse();
    else if (evt.state.clickCount === 2)
      this.collapse();
  }
}
