import { Morph } from "lively.morphic";
import Master from "lively.mirror/master.js";
import L2LClient from "lively.2lively/client.js";
import { connect, disconnect } from "lively.bindings/index.js";
import { promise } from "lively.lang/index.js";

export default class Rectangle extends Morph {

  async reset() {
    this.stopStepping();
    if (this.mirrorMaster) {
      try {
        await this.mirrorMaster.disconnect()
      } catch (e) { $world.logError(e); }
    }
    this.mirrorMaster = null;
    this.get("statusLabel").textString = "disconnected";
    this.get("openClientButton").label = "open client";
    this.get("connectButton").label = "connect...";
    disconnect(this.get("connectButton"), 'fire', this, 'reset');
    connect(this.get("connectButton"), 'fire', this, 'interactivelyConnectToMirrorClient');
    connect(this.get("openClientButton"), 'fire', this, 'openClientBrowserWindow');
  }

  async interactivelyConnectToMirrorClient() {
    // await l2lClient.register()

    if (this.mirrorMaster) await this.reset();

    // -=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-
    // 1. select a l2l connection for the client
    var clients = await this.findMirrorClients(),
        {selected: [clientL2lId]} = await $world.listPrompt("select client id", clients, {requester: this});

    if (!clientL2lId) return this.reset();

    // 2. initialize the master
    var masterId = "default-mirror-master",
        clientId = "__default__";
    this.mirrorMaster = Master.createInstance(masterId, $world, null, clientId);
    await this.mirrorMaster.l2lSetup(clientL2lId);

    // 3. Updae the ui
    this.get("statusLabel").textString = "connected to " + clientL2lId;
    this.get("connectButton").label = "disconnect";
    connect(this.get("connectButton"), 'fire', this, 'reset');
    disconnect(this.get("connectButton"), 'fire', this, 'interactivelyConnectToMirrorClient');
    await promise.delay(500);

    // 4. start send process
    this.startStepping(100, "triggerMirrorUpdate");
  }

  openClientBrowserWindow() {
    window.open(`${document.location.origin}/mirror.html`, "_blank")
  }

  async findMirrorClients() {
    var l2lClient = L2LClient.default(),
        {data: clients} = await l2lClient.sendToAndWait(l2lClient.trackerId, "getClients", {}),
        m = new Map(clients),
        otherClients = []

    for (let [key, val] of m)
      if (key !== l2lClient.id)
        otherClients.push(key);

    return otherClients;
  }

  async triggerMirrorUpdate() {
    if (!this.mirrorMaster || this.mirrorMaster.sendInProgress) return;
    try {
      var answer = await this.mirrorMaster.sendUpdate();
      if (!answer) return; // no changes happened / were send;
      this.get("statusLabel").textString = JSON.stringify(answer.data);
    } catch (e) { $world.logError(e); }
  }

}
