import { Morph, show, CenteredTilingLayout } from "lively.morphic";
import { resource } from "lively.resources/index.js";
import { connect, disconnect, disconnectAll } from "lively.bindings/index.js";
import { pt, Color } from "lively.graphics/index.js";
import LoadingIndicator from "lively.morphic/components/loading-indicator.js";
import { promise } from "lively.lang/index.js";
import { AbstractPrompt } from "lively.morphic/components/prompts.js";

export default class SaveWorldDialog extends AbstractPrompt {

  static get properties() {

    return {

      isEpiMorph: {get() { return true}},

      worldName: {
        derived: true, after: ["submorphs"],
        get() { return this.ui.nameInput.textString; },
        set(name) { this.ui.nameInput.textString = name; }
      },

      ui: {
        derived: true, readOnly: true, after: ["submorphs"],
        get() {
          return {
            label: this.getSubmorphNamed("save-dialog-label"),
            cancelButton: this.getSubmorphNamed("cancelButton"),
            okButton: this.getSubmorphNamed("okButton"),
            nameInput: this.getSubmorphNamed("name input"),
            nameLabel: this.getSubmorphNamed("name label"),
            destinationChooser: this.getSubmorphNamed("destination chooser"),
            destinationChooserLabel: this.getSubmorphNamed("destination chooser label")
          }
        }
      },
    }

  }

  alignInWorld(world = this.world()) {
    world.addMorph(this);
    let {width, height} = world.visibleBounds();
    this.width = (width*1/2) - 5;
    this.height = (height*1/2) - 10
    this.center = world.visibleBounds().center();
    return this;
  }

  reset() {
    // this.reset();
    // this.update();
    // this.openInWorld()

    this.layout = null;
    this.clipMode = "hidden";

    this.ui.label.fontColor = Color.white
    this.ui.label.fontSize = 21;
    this.ui.label.value = "Save world";
    this.ui.label.fit()

    connect(this, 'extent', this, 'relayout')
    connect(this.ui.cancelButton, 'fire', this, 'reject');
    connect(this.ui.okButton, 'fire', this, 'resolve');

    this.ui.destinationChooser.items = ["local storage", "server"];
  }

  activate() {
    this.ui.nameInput.showHistItem("prev"); // FIXME, last saved world name
    return super.activate();
  }

  reject() {
    super.reject({name: null, destination: null});
  }

  resolve() {
    let name = this.ui.nameInput.input;
    if (!name) return this.reject();
    this.remove(); // hard remove to not have save dialog in serialization
    this.ui.nameInput.acceptInput();
    let destination = this.ui.destinationChooser.selection;
    return super.resolve({name, destination});
  }

  focus() {
    this.ui.nameInput.focus();
  }

  relayout() {
    let bounds = this.innerBounds();
    let {
      label,
      cancelButton,
      okButton,
      nameInput,
      nameLabel,
      destinationChooser,
      destinationChooserLabel
    } = this.ui;

    label.topCenter = bounds.topCenter().addXY(0, 12);
    okButton.bottomRight = bounds.bottomCenter().addXY(-12, -12);
    cancelButton.bottomLeft = bounds.bottomCenter().addXY(12, -12);

    nameLabel.topLeft = pt(20, label.bottom + 22);
    destinationChooserLabel.topLeft = nameLabel.bottomLeft.addXY(0, 20);

    let labelIndent = Math.max(nameLabel.width, destinationChooser.width)

    nameInput.leftCenter = pt(labelIndent + 10, nameLabel.center.y)
    nameInput.width = bounds.width - nameInput.left- 20;

    destinationChooser.leftCenter = pt(labelIndent + 10, destinationChooserLabel.center.y)
  }

  beforePublish() {
    // disconnectAll(this);
    this.reset();
  }

  worldResources() {
    let r = resource(System.decanonicalize("lively.morphic/worlds/"));
    return r.dirList(1, {exclude: ea => !ea.name().endsWith(".json")})
  }

  resourceFor(name) {
    return resource(System.decanonicalize(`lively.morphic/worlds/${name}.json`))
  }

}
