import { Morph, show, CenteredTilingLayout } from "lively.morphic";
import { resource } from "lively.resources/index.js";
import { connect, noUpdate, disconnect, disconnectAll } from "lively.bindings/index.js";
import { pt, rect, Rectangle, Color } from "lively.graphics/index.js";
import LoadingIndicator from "lively.morphic/components/loading-indicator.js";
import { promise } from "lively.lang/index.js";

export default class WorldList extends Morph {

  static get properties() {

    return {

      isEpiMorph: {get() { return true}},

      ui: {
        derived: true, readOnly: true, after: ["submorphs"],
        get() {
          return {
            label: this.getSubmorphNamed("world-list-label"),
            cancelButton: this.getSubmorphNamed("cancelButton"),
            worldList: this.getSubmorphNamed("worldList"),
            locationTabs: this.getSubmorphNamed("location tabs"),
          }
        }
      },

      location:{
        derived: true, after: ["ui"],
        get() { return this.ui.locationTabs.activeTab; },
        set(location) { this.ui.locationTabs.activeTab = location; }
      },

      previews: {
        derived: true, after: ["worldList"],
        get() { return this.ui.worldList.submorphs; },
        set(val) { return this.ui.worldList.submorphs = val; }
      }
    }

  }

  relayout() {
    let bounds = this.innerBounds(),
        {
          label,
          cancelButton,
          worldList,
          locationTabs
        } = this.ui;

    label.topCenter = bounds.topCenter().addXY(0, 5)
    locationTabs.topCenter = label.bottomCenter.addXY(0, 7)
    cancelButton.bottomCenter = bounds.bottomCenter().addXY(0, -5);
    worldList.setBounds(
      rect(pt(0, locationTabs.bottom), pt(bounds.width, cancelButton.top-5)));
  }

  alignInWorld(world = this.world()) {
    world.addMorph(this);
    let {width, height} = world.visibleBounds();
    this.width = (width*1/2) - 5;
    this.height = (height*1/2) - 10
    this.center = world.visibleBounds().center();
    return this;
  }

  reset() {
    // this.reset();
    // this.update();

    // this.submorphs = [];
    // connect(this, 'extent', this, 'relayout');

    this.clipMode = "hidden";

    let {ui: {label, worldList, cancelButton, locationTabs}} = this;

    locationTabs.tabs = ["public", "local"];
    locationTabs.theme = "dark";
    noUpdate({
      sourceObj: locationTabs, sourceAttribute: "activeTab",
      targetObj: this,  targetAttribute: "saveLocationChoice"
    }, () => this.location = null);

    label.fontColor = Color.white
    label.fontSize = 26;
    label.value = "Select a world to load";
    label.fit()


    worldList.layout = new CenteredTilingLayout({spacing: 15, border:{top: 10, bottom: 10}});
    worldList.clipMode = "auto";
    worldList.fill = Color.rgbHex("777");
    worldList.draggable = false;

    cancelButton.label = "close"
    cancelButton.activeStyle = {
      borderWidth: 2,
      fill: Color.transparent,
      borderColor: Color.red.lighter(),
      fontColor: Color.red.lighter(),
      fontStyle: "bold",
      nativeCursor: "pointer"
    }

    connect(locationTabs, 'activeTab', this, 'saveLocationChoice');
    connect(locationTabs, 'activeTab', this, 'update');
    connect(cancelButton, 'fire', this, 'remove');
  }

  beforePublish() {
    // disconnectAll(this);
    this.reset();
  }

  async update() {
    // await this.update()

    this.previews = [];

    let location = this.location;
    if (!location) return;

    let i = LoadingIndicator.open("fetching worlds..."),
        rs = await this.worldResources(location);
    promise.finally(
      Promise.all(
        rs.map(r => this.addPreviewMorph(r)
          .catch(err => this.world().showError(err)))),
      () => i.remove());
    return this;
  }

  worldResources(location) {
    let urls = location === "public" ?
      [System.decanonicalize("lively.morphic/worlds/")] :
      location === "local" ? ["lively.storage://worlds/"] : []
    return Promise.all(urls.map(ea => resource(ea).dirList(1, {exclude: ea => !ea.name().endsWith(".json")})))
      .then(results => [].concat.apply([], results));
  }

  async resourceFor(location, name) {
    let url = location === "public" ?
      System.decanonicalize("lively.morphic/worlds/") :
      location === "local" ? "lively.storage://worlds/" : "UNNNNKNOWNNNN"
    return resource(url).join(`${name}.json`)
  }

  async addPreviewMorph(resource) {
    // let resource = (await this.worldResources())[0]

    let snapshot = await resource.readJson(),
        name = resource.name().replace(/\.json$/, "");

    let preview = this.previewFor(name) || this.ui.worldList.addMorph({
      name: "preview", extent: pt(200,200),
      nativeCursor: "pointer",
      submorphs: [
        {type: "image", reactsToPointer: false, autoResize: true},
        {type: "label", value: name, reactsToPointer: false}
      ]
    });

    let [image, label] = preview.submorphs;

    image.imageUrl = snapshot.preview;
    // await image.loadPromise();
    await promise.waitFor(1000, () => image.naturalExtent);
    image.scale = Math.min((preview.width-10) / image.width, (preview.height-10) / image.height);

    image.topCenter = preview.innerBounds().topCenter().addXY(0, 5);
    label.bottomCenter = preview.innerBounds().bottomCenter().addXY(0, -5);

    connect(preview, 'onHoverIn', this, 'onHoverInOverPreview');
    connect(preview, 'onHoverOut', this, 'onHoverOutOfPreview');
    connect(preview, 'onMouseUp', this, 'onPreviewClicked');
  }

  previewFor(name) {
    return this.previews.find(ea => ea.submorphs[1].textString === name);
  }

  saveLocationChoice(loc) {
    localStorage["lively.morphic-load-world-list-last-location"] = loc;
  }

  setLocationToLastChoiceOr(altLocation) {
    let location = localStorage["lively.morphic-load-world-list-last-location"] || altLocation;
    if (this.location !== location) this.location = location;
  }

  async onPreviewClicked(evt) {
    let p = evt.targetMorph,
        name = p.submorphs[1].textString,
        r = await this.resourceFor(this.location, name);

    let newWorld = await this.world().execCommand("load world", {resource: r});
    this.alignInWorld(newWorld);
  }

  onHoverOutOfPreview(evt) {
    let p = evt.targetMorph;
    p.borderWidth = 0
  }

  onHoverInOverPreview(evt) {
    this.previews.forEach(ea => ea.borderWidth = 0);
    let p = evt.targetMorph;
    p.border = {width: 3, color: Color.orange.withA(0.7)}
  }

  async onWorldSaved(name) {
    let i = LoadingIndicator.open("updating..."),
        r = await this.resourceFor(this.location, name);
    await this.addPreviewMorph(r);
    i.remove();
  }
}

