import { Morph } from "lively.morphic/morph.js";
import { pt, Color, Rectangle } from "lively.graphics/index.js";
import { disconnect, connect, signal } from "lively.bindings/index.js";
import { arr } from "lively.lang/index.js";
import { show } from "lively.morphic/index.js";

export default class TabButtons extends Morph {

  static get properties() {
    return {
      equalWidth: {defaultValue: true},
      tabs: {defaultValue: []},
      padding: {defaultValue: Rectangle.inset(8, 5)},
      fontFamily: {defaultValue: "sans-serif"},
      fontSize: {defaultValue: "14"},
      activeTab: {},
      theme: {defaultValue: "light"},
      buttons: {
        derived: true, after: ["submorphs"],
        get() { return this.submorphs; },
        set() {
          this.buttons.forEach(ea => {
            disconnect(ea, "onMouseUp", this, "onTabClicked");
          });
          this.submorphs = [];
        }
      }
    }
  }

  reset() {
    this.fill = null;
    this.fontSize = 14
    this.theme = "light";

    // let props = this.constructor.properties, keys = Object.keys(props);
    // keys.forEach(key => typeof this[key] === "undefined" && (this[key] = props[key].defaultValue))

    this.buttons = [];
    connect(this, 'activeTab', this, 'activateTab');
    connect(this, 'tabs', this, 'update');
    connect(this, 'theme', this, 'update');
    connect(this, 'equalWidth', this, 'update');
    connect(this, 'fontFamily', this, 'update');
    connect(this, 'fontSize', this, 'update');
    connect(this, 'padding', this, 'update');
    this.tabs = ["tab 1", "tab 2", "tab 3"]
  }

  styleForTheme(theme = "light") {
    let {padding, fontFamily, fontSize} = this,
        base = {
          padding, fontSize, fontFamily, borderRadius: 0,
          nativeCursor: "pointer",
          border: {width: 1, color: Color.gray}
        };
    if (theme === "light") {
      return {
        ...base,
        fontColor: Color.rgbHex("666"),
        fill: Color.white
      }
    }
    if (theme === "dark") {
      return {
        ...base,
        fontColor: Color.white,
        fill: Color.rgbHex("777"),
        border: {width: 0}
      }
    }
    return base;
  }

  update() {
    let {tabs} = this;


    if (!tabs.length) {
      this.extent = pt(20, 20);
      this.buttons = [];
      return;
    }

    let {buttons, padding, fontFamily, fontSize} = this;

    buttons.slice(tabs.length).forEach(ea => {
      disconnect(ea, "onMouseUp", this, "onTabClicked");
      ea.remove();
    });

    let x = 0, maxWidth = 0, maxHeight = 0;

    tabs.forEach((tabName, i) => {
      let btn = buttons[i],
          props = {...this.styleForTheme(this.theme), value: tabName, left: x};
      if (btn) {
        Object.assign(buttons[i], props);
      } else {
        btn = this.addMorph({type: "label", ...props});
        connect(btn, "onMouseUp", this, "onTabClicked",
          {converter: function() { return this.sourceObj; }});
      }
      btn.fit();
      x += Math.round(btn.width);
      maxWidth = Math.round(Math.max(maxWidth, btn.width));
      maxHeight = Math.round(Math.max(maxHeight, btn.height));
    });

    this.height = maxHeight;
    if (this.equalWidth) {
      this.width = this.submorphs.reduce((left, ea) => {
        ea.left = left;
        ea.width = maxWidth
        return ea.right;
      }, 0);
    } else {
      this.width = x;
    }


    let first = this.buttons[0],
        last = arr.last(this.buttons);

    first.borderRadiusLeft = 10;
    first.borderRadiusTop = 10;
    first.borderRadiusBottom = 0;
    first.borderRadiusRight = 0;
    last.borderRadiusLeft = 0;
    last.borderRadiusTop = 10;
    last.borderRadiusBottom = 0;
    last.borderRadiusRight = 10;

    this.setProperty("activeTab", null);
  }

  onTabClicked(tabButton) {
    this.activateTab(tabButton);
  }

  activateTab(tab) {
    let {buttons} = this,
        tabButton = !tab ? null : tab.isMorph ?
          tab : buttons.find(ea => ea.textString === tab),
        baseBorder = this.styleForTheme(this.theme).border;
    arr.without(buttons, tabButton).forEach(ea => {
      ea.borderBottom = baseBorder;
    });
    if (tabButton) {
      this.activeTab = tabButton.textString;
      tabButton.borderBottom = {width: 4, color: Color.tangerine};
    } else {
      this.activeTab = null
    }
  }

}
