import { Morph, show, CenteredTilingLayout } from "lively.morphic";
import { resource } from "lively.resources/index.js";
import { connect, disconnectAll } from "lively.bindings/index.js";
import { pt, Color } from "lively.graphics/index.js";
import LoadingIndicator from "lively.morphic/components/loading-indicator.js";
import { promise } from "lively.lang/index.js";

export default class WorldList extends Morph {

  static get properties() {

    return {

      isEpiMorph: {get() { return true}},

      label: {
        derived: true, readOnly: true, after: ["submorphs"],
        get() {
          let l = this.getSubmorphNamed("world-list-label");
          if (l) return l;
          return this.addMorph({
            type: "label", name: "world-list-label"
          });
        }
      },

      cancelButton: {
        derived: true, readOnly: true, after: ["submorphs"],
        get() {
          let l = this.getSubmorphNamed("cancelButton");
          if (l) return l;
          return this.addMorph({
            type: "button", name: "cancelButton",
            label: "cancel"
          });
        }
      },

      refreshButton: {
        derived: true, readOnly: true, after: ["submorphs"],
        get() { return this.getSubmorphNamed("refresh button"); }
      },

      worldList: {
        derived: true, readOnly: true, after: ["submorphs"],
        get() {
          let l = this.getSubmorphNamed("worldList");
          if (l) return l;
          return this.addMorph({
            name: "worldList"
          });
        }
      },

      previews: {
        derived: true, readOnly: true, after: ["worldList"],
        get() { return this.worldList.submorphs; }
      }
    }

  }

  alignInWorld(world = this.world()) {
    world.addMorph(this);
    let {width, height} = world.visibleBounds();
    this.width = (width*1/2) - 5;
    this.height = (height*1/2) - 10
    this.center = world.visibleBounds().center();
    return this;
  }

  reset() {
    // this.reset();
    // this.update();

    // this.submorphs = [];

    this.layout = null;
    this.clipMode = "hidden";

    this.label.fontColor = Color.white
    this.label.fontSize = 30;
    this.label.value = "Select a world to load";
    this.label.fit()

    connect(this, 'extent', this.label, 'topCenter', {
      converter: function(val) { return val.addXY(-val.x/2, -val.y + 12); }
    }).update(this.extent);

    this.worldList.submorphs = [];
    this.worldList.layout = new CenteredTilingLayout({spacing: 15});    
    this.worldList.layout.border.top = 10;
    this.worldList.layout.border.bottom = 10;
    this.worldList.layout.apply()
    this.worldList.extent = pt(300,300)
    this.worldList.clipMode = "auto";
    this.worldList.fill = null;
    this.worldList.draggable = false;

    this.cancelButton.label = "close"
    this.cancelButton.activeStyle = {
      borderWidth: 2,
      fill: Color.transparent,
      borderColor: Color.red.lighter(),
      fontColor: Color.red.lighter(),
      fontStyle: "bold",
      nativeCursor: "pointer"
    }

    connect(this, 'extent', this.cancelButton, 'bottomCenter', {
      converter: function(val) { return val.addXY(-val.x/2, -10); }
    }).update(this.extent);

    connect(this, 'extent', this.refreshButton, 'topRight', {
      converter: function(val) { return val.addXY(-40, -val.y + 20); }
    }).update(this.extent);

    connect(this.cancelButton, 'fire', this, 'remove');

    connect(this, 'extent', this.worldList, 'setBounds', {
      converter: function(val) {
        let Point = val.constructor;
        let {label, cancelButton} = this.sourceObj;
        return new Point(0, label.bottom)
          .extent(new Point(val.x, val.y - (60+cancelButton.height+label.height)))
      }
    }).update(this.extent);

    connect(this.refreshButton, 'fire', this, 'update');
  }

  beforePublish() {
    // disconnectAll(this);
    this.reset();
  }

  async update() {
    // await this.update()
    this.reset();
    let i = LoadingIndicator.open("fetching worlds...");
    let rs = await this.worldResources();
    promise.finally(
      Promise.all(
        rs.map(r => this.addPreviewMorph(r)
          .catch(err => $world.showError(err)))),
      () => i.remove())
    return this;
  }

  worldResources() {
    let urls = [
      "lively.storage://worlds/",
      System.decanonicalize("lively.morphic/worlds/")
    ];
    return Promise.all(urls.map(ea => resource(ea).dirList(1, {exclude: ea => !ea.name().endsWith(".json")})))
      .then(results => [].concat.apply([], results));
  }

  async resourceFor(name) {
    var r = resource(System.decanonicalize(`lively.morphic/worlds/${name}.json`))
    if (await r.exists()) return r;
    r = resource(System.decanonicalize(`lively.storage://worlds/${name}.json`))
    if (await r.exists()) return r;
    return null;
  }

  async addPreviewMorph(resource) {
    // let resource = (await this.worldResources())[0]

    let snapshot = await resource.readJson(),
        name = resource.name().replace(/\.json$/, "");

    let preview = this.previewFor(name) || this.worldList.addMorph({
      name: "preview", extent: pt(200,200),
      nativeCursor: "pointer",
      submorphs: [
        {type: "image", reactsToPointer: false, autoResize: true},
        {type: "label", value: name, reactsToPointer: false}
      ]
    });

    let [image, label] = preview.submorphs;

    image.imageUrl = snapshot.preview;
    // await image.loadPromise();
    await promise.waitFor(1000, () => image.naturalExtent);
    image.scale = Math.min((preview.width-10) / image.width, (preview.height-10) / image.height);

    image.topCenter = preview.innerBounds().topCenter().addXY(0, 5);
    label.bottomCenter = preview.innerBounds().bottomCenter().addXY(0, -5);

    connect(preview, 'onHoverIn', this, 'onHoverInOverPreview');
    connect(preview, 'onHoverOut', this, 'onHoverOutOfPreview');
    connect(preview, 'onMouseUp', this, 'onPreviewClicked');

  }

  previewFor(name) {
    return this.previews.find(ea => ea.submorphs[1].textString === name);
  }

  async onPreviewClicked(evt) {
    let p = evt.targetMorph,
        name = p.submorphs[1].textString,
        r = await this.resourceFor(name);

    let newWorld = await this.world().execCommand("load world", {resource: r});
    this.alignInWorld(newWorld);
  }

  onHoverOutOfPreview(evt) {
    let p = evt.targetMorph;
    p.borderWidth = 0
  }

  onHoverInOverPreview(evt) {
    this.previews.forEach(ea => ea.borderWidth = 0);
    let p = evt.targetMorph;
    p.border = {width: 3, color: Color.orange.withA(0.7)}
  }

  async onWorldSaved(name) {
    let i = LoadingIndicator.open("updating...");
    let r = await this.resourceFor(name);
    await this.addPreviewMorph(r);
    i.remove();
  }
}
